$(document).ready((function(){function countdata(){$.ajax({url:"./php_operations/newadmissions.php",method:"POST",data:{type:"getcount"},success:function(data){$("#getcountdata").html(data)}})}function load_data(page,query=""){$.ajax({url:"./php_operations/newadmissions.php",method:"POST",data:{page:page,query:query,type:"loaddata"},success:function(data){$("#dynamic_content").html(data)}})}countdata(),setInterval((function(){countdata()}),1e3),load_data(1)}));